    private void jButton14ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton14ActionPerformed
        // TODO add your handling code here:
        String msg = jTextField6.getText();
       //String email = txt_email.getText();
        // new AppShell(msg).setVisible(true);

        jLabel62.setText(msg);
         String emailRegex = "^[a-zA-Z0-9_+&*-]+(?:\\."+ 
                            "[a-zA-Z0-9_+&*-]+)*@" + 
                            "(?:[a-zA-Z0-9-]+\\.)+[a-z" + 
                            "A-Z]{2,7}$";
         
         String phonevalid = "^[0-9]{10}$";
         
        String email = txt_email.getText();
        String phoneNo = phone.getText();
        
        Pattern pattern = Pattern.compile(emailRegex);
        Pattern pattern2 = Pattern.compile(phonevalid);
        
        boolean valid = pattern.matcher(email).matches();
         boolean valid2 = pattern2.matcher(phoneNo).matches();

        if(jTextField6.getText().equals("") || txt_email.getText().equals("") || phone.getText().equals("")){
            playSound("pressSound.wav");
            JOptionPane.showMessageDialog(null, "Field empty, Fill all fields and try again!");
            contentPanel.removeAll();
            contentPanel.repaint();
            contentPanel.revalidate();

            contentPanel.add(EditContact);
            contentPanel.repaint();
            contentPanel.revalidate();
        }
        else if(!valid){
           JOptionPane.showMessageDialog(null, "Enter Valid Email Address!");
           playSound("pressSound.wav");
           
            }
        else if(!valid2){
           JOptionPane.showMessageDialog(null, "Enter Valid phone number!");
           playSound("pressSound.wav");
           
            }
        
        else{

        JOptionPane.showMessageDialog(null, "Contact saved successfully!");
        playSound("pressSound2.wav");
        } 
    }//GEN-LAST:event_jButton14ActionPerformed

